import { ArrowBack, ArrowForward } from "@mui/icons-material";
import {
  Box,
  Card,
  Pagination,
  Stack,
  TablePagination,
  Typography,
} from "@mui/material";
import { useEffect, useState } from "react";
import Carousel from "react-material-ui-carousel";

const Home = () => {
  const [page, setPage] = useState(1);
  const itemsPerPage = 20;
  const [startIndex, setStartIndex] = useState(0);
  const [endIndex, setEndIndex] = useState(20);
  const handleChange = (event, value) => {
    setPage(() => value);
    const firstItem = (page - 1) * itemsPerPage;
    setStartIndex(firstItem);
    setEndIndex(firstItem + itemsPerPage);
    // You can also fetch data for the new page here
    console.log("pressed", startIndex);
  };

  const array = new Array(100).fill(8);
  const totalPages = Math.ceil(array.length / itemsPerPage);

  useEffect(() => {
    // Scroll to the top when the component mounts
    window.scrollTo(0, 0);
  }, []);
  // api = 'AIzaSyCRRLDe0UQGmY9tRulQnTls8h1OoHJ3zhA'
  return (
    <div style={{ display: "flex", flexDirection: "row" }}>
      {/* <Carousel NextIcon={<ArrowForward />} PrevIcon={<ArrowBack />}>
        {slides.map((data, i) => (
          <Box
            sx={{
              //   padding: 10,
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
              width: "100%",
              height: "50vh",
              backgroundImage: `url(${data})`,
              backgroundRepeat: "no-repeat",
              backgroundPosition: "center",
              backgroundSize: "cover",
              textAlign: "center",
              fontSize: "5vw",
              color: "#fff",
            }}
          >
            {i}
          </Box>
        ))}
      </Carousel> */}
      <Stack>
        {array.map(
          (data, i) =>
            i > startIndex &&
            i < endIndex && (
              <Typography variant="body1" sx={{ display: "inline" }}>
                {i}
              </Typography>
            )
        )}
        <Pagination count={totalPages} page={page} onChange={handleChange} />
      </Stack>
    </div>
  );
};

export default Home;
