import { ArrowBack, ArrowForward } from "@mui/icons-material";
import { Box, Card } from "@mui/material";
import Carousel from "react-material-ui-carousel";

const Home = () => {
  const slides = [
    `https://cdn.wallpapersafari.com/15/5/gwrUlW.jpg`,
    "https://wallpapers.com/images/hd/very-rough-blue-texture-npbrxmygmpzvkxfx.jpg",
    "https://www.creativefabrica.com/wp-content/uploads/2021/06/15/Metallic-blue-texture-background-Graphics-13430044-1.jpg",
    "https://www.google.com/url?sa=i&url=https%3A%2F%2Flearnphotographycanada.com%2Fawesome-bedroom-wallpaper-texture-black-as-40-grey-texture-background-and-wallpaper-for-designer-web-design%2F&psig=AOvVaw3G0kaMdu5KZprzkI1msO_i&ust=1710243280563000&source=images&cd=vfe&opi=89978449&ved=0CBMQjRxqFwoTCKj0le2O7IQDFQAAAAAdAAAAABAE",
  ];
  return (
    <div style={{ display: "flex", flexDirection: "column" }}>
      <Carousel NextIcon={<ArrowForward />} PrevIcon={<ArrowBack />}>
        {slides.map((data, i) => (
          <Box
            sx={{
              //   padding: 10,
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
              width: "100%",
              height: "50vh",
              backgroundImage: `url(${data})`,
              backgroundRepeat: "no-repeat",
              backgroundPosition: "center",
              backgroundSize: "cover",
              textAlign: "center",
              fontSize: "5vw",
              color: "#fff",
            }}
          >
            {i}
          </Box>
        ))}
      </Carousel>
    </div>
  );
};

export default Home;
